  ),
  Close = colDef(
    format = colFormat(currency = "USD", separators = TRUE, locales = "en-US")
  ),
  `Adj Close` = colDef(
    format = colFormat(currency = "USD", separators = TRUE, locales = "en-US")
  ), 
 Volume = colDef(
    format = colFormat(separators = TRUE, locales = "en-US")
  )
))
